import { useState } from "react";
import axios from "axios";
import { FcGoogle } from "react-icons/fc";
import { Button } from "/components/ui/button";
import { Card, CardContent } from "/components/ui/card";
import { Input } from "/components/ui/input";

export default function AuthPage() {
  const [isSignUp, setIsSignUp] = useState(false);
  const [formData, setFormData] = useState({ username: "", email: "", password: "" });

  const toggleMode = () => setIsSignUp(!isSignUp);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const endpoint = isSignUp ? "/api/user/signup" : "/api/user/signin";
    try {
      const response = await axios.post(endpoint, formData, {
        headers: { "Content-Type": "application/json" },
      });
      alert(isSignUp ? "Sign-up successful!" : "Sign-in successful!");
    } catch (error) {
      alert(error.response?.data?.message || "Something went wrong");
    }
  };

  return (
    <div className="flex min-h-screen items-center justify-center bg-gray-100">
      <Card className="w-full max-w-sm p-6 shadow-lg bg-white rounded-xl">
        <h2 className="text-2xl font-semibold text-center mb-4">
          {isSignUp ? "Sign Up" : "Sign In"}
        </h2>
        <CardContent>
          <form onSubmit={handleSubmit} className="space-y-4">
            {isSignUp && (
              <Input
                type="text"
                name="username"
                placeholder="Username"
                value={formData.username}
                onChange={handleChange}
                required
              />
            )}
            <Input
              type="email"
              name="email"
              placeholder="Email"
              value={formData.email}
              onChange={handleChange}
              required
            />
            <Input
              type="password"
              name="password"
              placeholder="Password"
              value={formData.password}
              onChange={handleChange}
              required
            />
            <Button type="submit" className="w-full">
              {isSignUp ? "Sign Up" : "Sign In"}
            </Button>
          </form>
          <div className="flex items-center my-4">
            <hr className="flex-grow border-gray-300" />
            <span className="px-2 text-gray-500">or</span>
            <hr className="flex-grow border-gray-300" />
          </div>
          <Button className="w-full flex items-center justify-center bg-white border border-gray-300 text-gray-700 hover:bg-gray-100">
            <FcGoogle className="mr-2 text-xl" /> Sign in with Google
          </Button>
          <p className="text-sm text-center mt-4">
            {isSignUp ? "Already have an account? " : "Don't have an account? "}
            <span
              className="text-blue-600 cursor-pointer"
              onClick={toggleMode}
            >
              {isSignUp ? "Sign In" : "Sign Up"}
            </span>
          </p>
        </CardContent>
      </Card>
    </div>
  );
}
